import { FC } from "react";
import { useKeyboardForm } from "../../lib/keyboardFormStore";
import { Flex, BorderBox, Sans, Grid } from "@rsbear/betsy";
import { useTheme } from "emotion-theming";

import { ITheme } from "../../lib/types";
import { Input } from "../UI/Input";

interface P {
  setStepOneComplete: Function;
}

const StepOne: FC<P> = ({ setStepOneComplete }) => {
  const { values, setString } = useKeyboardForm();
  const theme: ITheme = useTheme();

  return (
    <div>
      <BorderBox
        p="10px 20px"
        my="20px"
        border={`solid 1px ${theme.colors.dividerColor}`}
        borderRadius="4px"
      >
        <Sans fontWeight={600} fontSize="14px">
          Name &amp; Brand
        </Sans>
        <Grid preset={2} columnGap={15}>
          <Input
            icon="at-sign"
            placeholder="Name"
            name="name"
            value={values.name}
            onChange={(e) => setString(e)}
          />
          <Input
            icon="at-sign"
            placeholder="Brand"
            name="brand"
            value={values.brand}
            onChange={(e) => setString(e)}
          />
        </Grid>
      </BorderBox>

      <BorderBox
        p="10px 20px"
        my="20px"
        border={`solid 1px ${theme.colors.dividerColor}`}
        borderRadius="4px"
      >
        <Sans fontWeight={600} fontSize="14px">
          Size, Mount, &amp; Typing Angle
        </Sans>
        <Grid preset={3} columnGap={15}>
          <Input
            icon="at-sign"
            placeholder="Size"
            name="size"
            value={values.size}
            onChange={(e) => setString(e)}
          />
          <Input
            icon="at-sign"
            placeholder="Mount"
            name="mount"
            value={values.mount}
            onChange={(e) => setString(e)}
          />
          <Input
            icon="at-sign"
            placeholder="Typing Angle"
            name="angle"
            value={values.angle}
            onChange={(e) => setString(e)}
          />
        </Grid>
      </BorderBox>

      <Flex>
        <input
          type="text"
          placeholder="size"
          name="size"
          value={values.size}
          onChange={(e) => setString(e)}
        />
        <input
          type="text"
          placeholder="mount"
          name="mount"
          value={values.mount}
          onChange={(e) => setString(e)}
        />
        <input
          type="text"
          placeholder="typing angle"
          name="angle"
          value={values.angle}
          onChange={(e) => setString(e)}
        />
      </Flex>

      <Flex>
        <input
          type="text"
          placeholder="connection"
          name="connection"
          value={values.connection}
          onChange={(e) => setString(e)}
        />
        <input
          type="text"
          placeholder="pcb"
          name="pcb"
          value={values.pcb}
          onChange={(e) => setString(e)}
        />
        <input
          type="text"
          placeholder="firmware"
          name="firmware"
          value={values.firmware}
          onChange={(e) => setString(e)}
        />
      </Flex>

      <p>do you want to provide colors</p>

      <button onClick={() => setStepOneComplete()}>next</button>
    </div>
  );
};

export default StepOne;
