import { FC } from "react";
import { useKeyboardForm } from "../../lib/keyboardFormStore";
import { Flex, BorderBox, Sans, Grid, Button } from "@rsbear/betsy";
import { useThemer } from "../../hooks/useThemer";
import { Input } from "../UI/Input";

/** @jsx jsx */
import { jsx, css } from "@emotion/core";
import { ITheme } from "../../lib/types";

// extraPcbs: boolean;
// extraPlates: boolean;
// layouts: string[] | null;
// switchSupport?: string[] | null;
// editions: string[] | null;
// cases: ICase[];
// plates: IPlate[];

interface P {
  setStepTwoComplete: Function;
}

const StepTwo: FC<P> = ({ setStepTwoComplete }) => {
  const { values, setBoolean, setString, setObjArray } = useKeyboardForm();
  const theme = useThemer();

  return (
    <>
      <BorderBox
        p="10px 20px"
        my="20px"
        border={`solid 1px ${theme.colors.dividerColor}`}
        borderRadius="4px"
      >
        <Sans fontWeight={600} fontSize="14px">
          Layouts
        </Sans>
        <Grid css={() => gridify(theme)} columnGap={15}>
          <Input
            icon="at-sign"
            placeholder="Layout e.g. WKL, WK, HHKB"
            name="layouts"
            value={values.connection}
            onChange={(e) => setString(e)}
          />
          <Button
            text="Add another"
            onClick={() => {}}
            variant="secondary"
            height="36px"
            color={theme.colors.primaryAccent}
          />
        </Grid>
      </BorderBox>

      <BorderBox
        p="10px 20px"
        my="20px"
        border={`solid 1px ${theme.colors.dividerColor}`}
        borderRadius="4px"
      >
        <Sans fontWeight={600} fontSize="14px">
          Support
        </Sans>
        <Grid css={() => gridify(theme)} columnGap={15}>
          <Input
            icon="at-sign"
            placeholder="Support e.g. ANSI, ISO"
            name="support"
            value={values.support}
            onChange={(e) => setString(e)}
          />
          <Button
            text="Add another"
            onClick={() => {}}
            variant="secondary"
            height="36px"
            color={theme.colors.primaryAccent}
          />
        </Grid>
      </BorderBox>

      <Flex>
        <span>Will extra plates be available?</span>
        <button
          type="button"
          name="extraPlates"
          value={1}
          onClick={(e) => setBoolean(e)}
        >
          {!values.extraPlates ? "yes" : "yes already selected"}
        </button>
        <button
          type="button"
          name="extraPlates"
          value={null}
          onClick={(e) => setBoolean(e)}
        >
          {!values.extraPlates ? "no - selected" : "no"}
        </button>
      </Flex>

      <input
        type="text"
        name="cases"
        tabIndex={0}
        onChange={(e) => setObjArray(e)}
      />

      <button onClick={() => setStepTwoComplete()}>next</button>
    </>
  );
};

export default StepTwo;

const gridify = (theme: ITheme) => css`
  grid-template-columns: 1fr 140px;
  grid-column-gap: 15px;

  button {
    border-color: ${theme.colors.primaryAccent};
  }
`;
